// Verify webhook signature
const crypto = require('crypto');

const input = $input.first().json;
const receivedSignature = input.headers['x-webhook-signature'];
const body = input.body;

// Secret must match the one in Vercel environment
const secret = 'YOUR_WEBHOOK_SECRET_HERE';

// Recreate the signature from the body
const normalizedBody = JSON.stringify(body);
const expectedSignature = crypto
  .createHmac('sha256', secret)
  .update(normalizedBody)
  .digest('hex');

if (receivedSignature !== expectedSignature) {
  throw new Error('Invalid webhook signature');
}

// Signature valid - pass the body through
return { json: body };

const body = $input.first().json;

const cleanedData = {
  name: body.name?.trim() || '',
  email: body.email?.trim().toLowerCase() || '',
  phone: body.phone?.trim() || '',
  countryCode: body.countryCode || '+91',
  fullPhone: body.fullPhone || '',
  linkedin: body.linkedin?.trim() || '',
  message: body.message?.trim() || '',
  timestamp: body.timestamp || new Date().toISOString(),
  source: body.source || 'vaibhav.bio'
};

cleanedData.formattedDate = new Date(cleanedData.timestamp).toLocaleString('en-IN', {
  timeZone: 'Asia/Kolkata',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

cleanedData.referenceId = `CF-${Date.now()}-${Math.random().toString(36).substr(2, 6).toUpperCase()}`;

// Calculate reminder time (24 hours from now)
const reminderTime = new Date(Date.now() + 24 * 60 * 60 * 1000);
cleanedData.reminderTimestamp = Math.floor(reminderTime.getTime() / 1000);
cleanedData.reminderFormatted = reminderTime.toLocaleString('en-IN', {
  timeZone: 'Asia/Kolkata',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

return { json: cleanedData };

